// src/components/FormPage.js
import React, { useState } from 'react';

const FormPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    address: '',
    phone: '',
    income: '',
    expenses: '',
    savings: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data Submitted:', formData);
    // Here you can add functionality to send formData to your backend
  };

  return (
    <div style={styles.container}>
      <h2>Fill in Your Personal and Financial Information</h2>
      <form onSubmit={handleSubmit} style={styles.form}>

        {/* Personal Information */}
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            style={styles.input}
            required
          />
        </label>

        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            style={styles.input}
            required
          />
        </label>

        <label>
          Address:
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
            style={styles.input}
            required
          />
        </label>

        <label>
          Phone:
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            style={styles.input}
            required
          />
        </label>

        {/* Financial Information */}
        <label>
          Income:
          <input
            type="number"
            name="income"
            value={formData.income}
            onChange={handleChange}
            style={styles.input}
            required
          />
        </label>

        <label>
          Expenses:
          <input
            type="number"
            name="expenses"
            value={formData.expenses}
            onChange={handleChange}
            style={styles.input}
            required
          />
        </label>

        <label>
          Savings:
          <input
            type="number"
            name="savings"
            value={formData.savings}
            onChange={handleChange}
            style={styles.input}
            required
          />
        </label>

        <button type="submit" style={styles.button}>Submit</button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center',
    padding: '20px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  input: {
    margin: '10px 0',
    padding: '10px',
    width: '300px',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
};

export default FormPage;
