// src/components/FormPage.js
import React, { useState } from 'react';

const FormPage = () => {
  const [formData, setFormData] = useState({
    income: '',
    expenses: '',
    savings: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data Submitted:', formData);
    // You can add functionality here to send the data to your backend
  };

  return (
    <div style={styles.container}>
      <h2>Enter Your Financial Details</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <label>
          Income:
          <input
            type="text"
            name="income"
            value={formData.income}
            onChange={handleChange}
            style={styles.input}
          />
        </label>
        <label>
          Expenses:
          <input
            type="text"
            name="expenses"
            value={formData.expenses}
            onChange={handleChange}
            style={styles.input}
          />
        </label>
        <label>
          Savings:
          <input
            type="text"
            name="savings"
            value={formData.savings}
            onChange={handleChange}
            style={styles.input}
          />
        </label>
        <button type="submit" style={styles.button}>Submit</button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center',
    padding: '20px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  input: {
    margin: '10px 0',
    padding: '10px',
    width: '300px',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
};

export default FormPage;
